function switchb(c){var a=document.getElementsByName("switch");var d="none";for(var b=0;b<a.length;b++){if(a[b].checked){d="block";break}}document.getElementById(c).style.display=d}var x;var count;var text=document.getElementById("TextBoxToCount").value;setInterval(function(){var f=0;var b=0;var g=0;var c=0;text=document.getElementById("TextBoxToCount").value;s=text;s=s.replace(/(^\s*)|(\s*$)/gi,"");s=s.replace(/[ ]{2,}/gi," ");s=s.replace(/\n /,"\n");x=s.split(" ").length;count=text.length;if(text==""){x=0}sentences=text;sentencesDot=sentences.replace(/\!/g,".").replace(/\?/g,".");sentencesCount=sentencesDot.split(".").length-1;for(var a=0;a<sentencesCount;a++){c++;j=text;j=j.replace(/(^\s*)|(\s*$)/gi,"");j=j.replace(/[ ]{2,}/gi," ");j=j.replace(/\n /,"\n");g=g+j.split(" ").length;b=b+sentencesDot.split(".")[a].replace(" ","").length;if(sentencesDot.split(".")[a]!=""){f++}}b=b/f;g=x/f;var d;if(text!=""){d=text.replace(/\n$/gm,"").split(/\n/).length}else{d=0}document.getElementById("TextInBoxCounter").innerHTML=count+" Characters | "+x+" Words | "+f+" Sentences | "+d+" Paragraphs";document.getElementById("sentences").innerHTML=f;if(f==0){document.getElementById("sentenceLength").innerHTML="0"}else{document.getElementById("sentenceLength").innerHTML=b.toFixed(2)+" Characters<br>"+g.toFixed(2)+" Words"}},100);
